import React from "react";
import contact from "../contacts";

function Card(props){
    return (
        <div className="card">
        <div className="top">
          <h2 className="name">{props.name}</h2>
          <img
            src={props.imgURL}
            alt="avatar_img"
            className="circle-img"
          />
        </div>
        <div className="bottom">
          <p className="info">{props.phone}</p>
          <p className="info">b@beyonce.com</p>
        </div>
      </div>
    )
}
export default Card;
